import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Container, 
  Typography, 
  Button, 
  Paper,
  Box,
  TextField
} from '@mui/material';

function LandingPage() {
  const navigate = useNavigate();
  const [companyProfile, setCompanyProfile] = useState({
    problemStatement: '',
    productDescription: '',
    additionalInfo: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    navigate('/claims', { state: { companyProfile } });
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ py: 4 }}>
        <Paper elevation={3} sx={{ p: 4 }}>
          <Typography variant="h4" gutterBottom>
            Company Profile
          </Typography>
          <form onSubmit={handleSubmit}>
            <TextField
              fullWidth
              multiline
              rows={4}
              variant="outlined"
              label="Problem Statement"
              value={companyProfile.problemStatement}
              onChange={(e) => setCompanyProfile(prev => ({
                ...prev,
                problemStatement: e.target.value
              }))}
              required
              sx={{ mb: 3 }}
            />
            <TextField
              fullWidth
              multiline
              rows={4}
              variant="outlined"
              label="Product Description"
              value={companyProfile.productDescription}
              onChange={(e) => setCompanyProfile(prev => ({
                ...prev,
                productDescription: e.target.value
              }))}
              required
              sx={{ mb: 3 }}
            />
            <TextField
              fullWidth
              multiline
              rows={4}
              variant="outlined"
              label="Additional Information"
              placeholder="Enter market research, technical specifications, competitive analysis, etc."
              value={companyProfile.additionalInfo}
              onChange={(e) => setCompanyProfile(prev => ({
                ...prev,
                additionalInfo: e.target.value
              }))}
              sx={{ mb: 3 }}
            />
            <Button 
              type="submit"
              variant="contained" 
              size="large"
              sx={{ 
                px: 4, 
                py: 1.5,
                fontSize: '1.2rem'
              }}
            >
              Start Analysis
            </Button>
          </form>
        </Paper>
      </Box>
    </Container>
  );
}

export default LandingPage;
