import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Container, 
  TextField, 
  Button, 
  Paper, 
  Box, 
  Typography,
  CircularProgress
} from '@mui/material';
import { generateClaims, getAIEvidence, getDocumentEvidence, uploadDocument } from '../services/apiClient';
import ClaimsList from '../components/ClaimsList';

function ClaimsPage() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [problemStatement, setProblemStatement] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [claims, setClaims] = useState([]);
  const [documents, setDocuments] = useState([]);

  // Fixed claims that are always present
  const fixedClaims = [
    {
      id: 'market-size',
      text: 'The target market for this product is sufficiently large',
      type: 'fixed',
      evidence: []
    },
    {
      id: 'market-growth',
      text: 'The target market is growing at a significant rate',
      type: 'fixed',
      evidence: []
    }
  ];

  const handleGenerateClaims = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const generatedClaims = await generateClaims(problemStatement, productDescription);
      // Combine fixed claims with generated claims
      setClaims([
        ...fixedClaims,
        ...generatedClaims.map((claim, index) => ({
          id: `generated-${index}`,
          text: claim,
          type: 'generated',
          evidence: []
        }))
      ]);
    } catch (error) {
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleAddManualEvidence = (claimId, evidence) => {
    setClaims(claims.map(claim => 
      claim.id === claimId 
        ? { ...claim, evidence: [...(claim.evidence || []), { type: 'manual', content: evidence }] }
        : claim
    ));
  };

  const handleAskAI = async (claimId) => {
    setLoading(true);
    try {
      const claim = claims.find(c => c.id === claimId);
      const evidence = await getAIEvidence(claim.text, { 
        problemStatement, 
        productDescription 
      });
      
      setClaims(claims.map(c => 
        c.id === claimId 
          ? { ...c, evidence: [...(c.evidence || []), { type: 'ai', content: evidence }] }
          : c
      ));
    } catch (error) {
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    try {
      const uploadedDoc = await uploadDocument(file);
      setDocuments([...documents, uploadedDoc]);
    } catch (error) {
      alert(error.message);
    }
  };

  const handleAskDocuments = async (claimId) => {
    if (documents.length === 0) {
      alert('Please upload documents first');
      return;
    }

    setLoading(true);
    try {
      const claim = claims.find(c => c.id === claimId);
      const evidence = await getDocumentEvidence(claim.text, documents.map(d => d.id));
      
      setClaims(claims.map(c => 
        c.id === claimId 
          ? { ...c, evidence: [...(c.evidence || []), { type: 'document', content: evidence }] }
          : c
      ));
    } catch (error) {
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleProceedToSummary = () => {
    // Check if all claims have evidence
    const allClaimsHaveEvidence = claims.every(claim => 
      claim.evidence && claim.evidence.length > 0
    );

    if (allClaimsHaveEvidence) {
      navigate('/summary', { state: { claims } });
    } else {
      alert('Please provide evidence for all claims before proceeding');
    }
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      {claims.length === 0 ? (
        <Paper elevation={3} sx={{ p: 4 }}>
          <Typography variant="h4" gutterBottom>
            Define Your Innovation
          </Typography>
          <form onSubmit={handleGenerateClaims}>
            <TextField
              fullWidth
              multiline
              rows={4}
              variant="outlined"
              label="Problem Statement"
              value={problemStatement}
              onChange={(e) => setProblemStatement(e.target.value)}
              required
              sx={{ mb: 3 }}
            />
            <TextField
              fullWidth
              multiline
              rows={4}
              variant="outlined"
              label="Product Description"
              value={productDescription}
              onChange={(e) => setProductDescription(e.target.value)}
              required
              sx={{ mb: 3 }}
            />
            <Button 
              type="submit" 
              variant="contained" 
              size="large"
              disabled={loading}
              sx={{ minWidth: 200 }}
            >
              {loading ? <CircularProgress size={24} /> : 'Generate Claims'}
            </Button>
          </form>
        </Paper>
      ) : (
        <Box>
          <Typography variant="h4" gutterBottom>
            Claims Analysis
          </Typography>
          <Paper elevation={3} sx={{ p: 4, mb: 3 }}>
            <ClaimsList
              claims={claims}
              onAddManualEvidence={handleAddManualEvidence}
              onAskAI={handleAskAI}
              onAskDocuments={handleAskDocuments}
            />
            
            <Box sx={{ mt: 3, p: 2, bgcolor: '#f5f5f5', borderRadius: 1 }}>
              <Typography variant="h6" gutterBottom>
                Upload Supporting Documents
              </Typography>
              <Button
                variant="contained"
                component="label"
                sx={{ mr: 2 }}
              >
                Upload File
                <input
                  type="file"
                  hidden
                  accept=".pdf,.csv"
                  onChange={handleFileUpload}
                />
              </Button>
              {documents.length > 0 && (
                <Typography variant="body2" color="text.secondary">
                  {documents.length} document(s) uploaded
                </Typography>
              )}
            </Box>

            <Box sx={{ mt: 3, display: 'flex', justifyContent: 'flex-end' }}>
              <Button 
                variant="contained"
                onClick={handleProceedToSummary}
                disabled={loading || !claims.every(claim => claim.evidence?.length > 0)}
                size="large"
              >
                Proceed to Summary
              </Button>
            </Box>
          </Paper>
        </Box>
      )}

      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      )}
    </Container>
  );
}

export default ClaimsPage;
